import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person'

class App extends Component {
  state =  {
    persons: [
      {name: 'Max', age: 28},
      {name: 'Manu', age: 29},
      {name: 'Chicken', age: 32}
    ],
    otherState: 'some other value'

  };

  switchNameHandler = (newName) => {
  //console.log('was clicked!')
    this.setState({
      persons: [
        {name: newName, age: 28},
        {name: 'Manu', age: 1234},
        {name: 'Chicken', age: 32}
      ]
    })
  }

  nameChangedHandler = (event) => {
    this.setState({
      persons: [
        {name: 'Max', age: 28},
        {name: event.target.value, age: 1234},
        {name: 'Chicken', age: 32}
      ]
    })
  }
  render(){
    return(
      <div className="App">
        <h1>Hi, I'm a react app</h1>
        <p>Hi dog!</p>
        <button onClick={()=>this.switchNameHandler('Maximilian')}>Switch Name</button>
        <Person 
          name={this.state.persons[0].name} 
          age={this.state.persons[0].age}
          click={this.switchNameHandler.bind(this,'Maximilian!!')}>My hobbies: racing</Person>
        <Person 
          name={this.state.persons[1].name} 
          age={this.state.persons[1].age}>My hobbies: racing</Person>
        <Person 
          name={this.state.persons[2].name} 
          age={this.state.persons[2].age}>My hobbies: racing</Person>



      </div>
    );
  }
    //return React.createElement('div',{className: 'App'},React.createElement('h1',null,'Hi min gmong'));

}


export default App;
