import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state =  {
    persons: [
      {id: 'asf1',  name: 'Max', age: 28},
      {id: 'asf21', name: 'Manu', age: 29},
      {id: 'asf11', name: 'Chicken', age: 32}
    ],
    otherState: 'some other value',
    showPersons:false
  };

  // switchNameHandler = (newName) => {
  // //console.log('was clicked!')
  //   this.setState({
  //     persons: [
  //       {name: newName, age: 28},
  //       {name: 'Manu', age: 1234},
  //       {name: 'Chicken', age: 32}
  //     ]
  //   })
  // }

  deletePersonHandler = (personIndex) => {
    //copies full array
    //const persons = this.state.persons.slice();
    const persons = [...this.state.persons];
    persons.splice(personIndex,1);
    this.setState({persons: persons});

  }

  nameChangedHandler = (event, id) => {
    //find person index
    const personIndex = this.state.persons.findIndex(p => {
      return p.id == id;
    });

    //copy person object
    const person = {
      ...this.state.persons[personIndex]
    };

    person.name = event.target.value;

    //copy persons array
    const persons = [...this.state.persons];
    persons[personIndex] = person;

    this.setState({persons: persons});
  }
  togglePersonsHandler = (event) =>{
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow});
  }
  render(){
    const style = {
      backgroundColor: 'green',
      color: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding:'8px',
      cursor: 'pointer'
    };

    let persons = null;

    if (this.state.showPersons){
      //jsx code
      persons = (
        //map persons array to jsx by taking a function and applying to every element in the array
        <div>
            {this.state.persons.map((person,index) =>{
              return <Person 
                click={() => this.deletePersonHandler(index)}
                name={person.name}
                age={person.age}
                key={person.id}
                changed={(event) => this.nameChangedHandler(event, person.id)}/>
            })}
            {/* <Person 
              name={this.state.persons[0].name} 
              age={this.state.persons[0].age}
              click={this.switchNameHandler.bind(this,'Maximilian!!')}>My hobbies: racing</Person>
            <Person 
              name={this.state.persons[1].name} 
              age={this.state.persons[1].age}
              changed={this.nameChangedHandler}>My hobbies: racing</Person>
            <Person 
              name={this.state.persons[2].name} 
              age={this.state.persons[2].age}>My hobbies: racing</Person> */}
          </div> 
      );
    }
    return(
      <div className="App">
        <h1>Hi, I'm a react app</h1>
        <p>Hi dog!</p>
        <button 
          style={style}
          onClick={this.togglePersonsHandler}>Switch Name</button>
          {persons}






      </div>
    );
  }
    //return React.createElement('div',{className: 'App'},React.createElement('h1',null,'Hi min gmong'));

}


export default App;
